import React, {useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; 
import { faEye, faEyeSlash } from "@fortawesome/free-solid-svg-icons"

export const LoginForm = () => {
    const {email, setEmail} = useState("")
    const {passwored, setPassword} = useState("")
    const {showPass, setShowPass} = useState(false)

    const {message, setMessage} = useState("")

    const togglePassword = (event) => {
        event.preventDefault();

        setShowPass(!showPass)
    }

    return (
        <React.Fragment>
            <label className='email'>email</label>
            <input
            type='text'
            className='email'
            onChange={(e) => setEmail(e.target.value)}
            ></input>
            <label className='password'>Password</label>
            <input
            type={showPass ? "test" : "password"}
            className='password'
            onChange={(e) => setPassword(e.target.value)}
            ></input>
            <span onClick={(e) => togglePassword(e)} style={{cursor: "pointer"}}>
                <span>
                    {showPass ? (
                        <FㅐㄱAwesoneIcon icon={faEye} className='customIcon' />
                    ) : (
                        <FontAwesoneIcon icon={faEyeSlash} className='customIcon' />
                    )
                }
                </span>
            </span>
        </React.Fragment>
    )
}

export default LoginForm
