import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import '../styles/LoginPage.css';
import LoginForm from "../compoents/LoginForm";

const LoginPage = () => {
    const [registerFormStatus, setRegistrationFormStatus] = useState(false);

    const loginProps = useSpring({
        left: registerFormStatus ? -500 : 0,
    });

    const registerProps = useSpring({
        left: registerFormStatus ? 0 : 500,
    });

    const loginButtonProps = useSpring({
        borderBottom: registerFormStatus ? "solid 0px transparent" : "solid 2px white"
    });

    const registerButtonProps = useSpring({
        borderBottom: registerFormStatus ? "solid 2px white" : "solid 0px transparent"
    });

    const loginClicked = () => {
        setRegistrationFormStatus(false)
    }

    const registerClicked = () => {
        setRegistrationFormStatus(true)
    }

    return (<div className="container">
        <div className="login-wrapper">
            <div className="nav-buttions">
                <animated.button id="loginButton" onClick={loginClicked} style={loginButtonProps}>
                    Login
                </animated.button>

            </div>
        </div>
    </div>
    );
};

export default LoginPage;
